;(function($){
  $.fn.rotate = function(options){
    //默认值
  var defaults = {
    Time:1000
  }
  var options = $.extend(defaults, options);

  this.each(function(){
    var thisR = $(this),
        $ul = thisR.find("ul"),
        $lis = $ul.children("li"),
        width = $lis.eq(0).width();
        
        var len = $ul.find("li").length;
        tw = (width + 40) * len;
        ti = 2000 * len;
        function timer(){
          setInterval(function(){
            $ul.animate({
              'margin-left':'-'+ tw +'px'
            },ti,
            function(){
              //轮播图片的个数

              });
            },0);
        };
        setInterval(timer);
        thisR.mouseenter(function(){
          clearInterval(timer);
        }).mouseleave(function(){
          setInterval(timer);
        });
  });

}})(jQuery);
